import { StatusBar } from "expo-status-bar";
import React, { useState, useEffect } from "react";
import { StyleSheet, View, Text, Animated, Dimensions } from "react-native";
import { LinearGradient } from 'expo-linear-gradient';


const AnimatedText = Animated.createAnimatedComponent(Text);

const ScreenLoad = () => {
    const { height } = Dimensions.get("window");

    const [animateY, setAnimateY] = useState(new Animated.Value(0));
    const [animateYwelcome, setAnimateYwelcome] = useState(new Animated.Value(0));
    const [animated, setAnimated] = useState(false);

    useEffect(() => {
        if (!animated) {
        Animated.timing(animateY, {
            toValue: height / 2,
            duration: 3000,
        }).start();

        Animated.timing(animateYwelcome, {
            toValue: height / 2 - 15,
            duration: 3000,
        }).start();
        }

        setAnimated(false);
    }, []);
    return (
        <View style={styles.container}>
            <LinearGradient
        // Background Linear Gradient
        colors={["#ffc78f", '#ff9c38']}
        style={styles.background}
      />
        <AnimatedText
            style={{
            top: animateY,
            position: "absolute",
            fontSize: 50,
            fontStyle: "italic",
            fontWeight: "bold",
            color: "white",
            right: 70
            }}
        >
            ME 
        </AnimatedText>


        <AnimatedText
            style={{
            bottom: animateYwelcome,
            position: "absolute",
            fontSize: 50,
            fontStyle: "italic",
            fontWeight: "bold",
            color: "white",
            }}
        >
            MENTOR
        </AnimatedText>

        <LinearGradient
        // Background Linear Gradient
        colors={['#ff9c38','#663300']}
        style={styles.bottomBackground}
      />

        <StatusBar style="auto" />
        </View>
    );
    }

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#ff9c38',
    },
    background: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        height: 300,
      },
      bottomBackground: {
        position: 'absolute',
        left: 0,
        right: 0,
        bottom: 0,
        height: 300,
      },

});

export default ScreenLoad
